class MinesweeperEnv2:
    def __init__(self, rows, cols, num_mines):
        self.rows = rows
        self.cols = cols
        self.num_mines = num_mines
        self.reset()

    def reset(self):
        # Khởi tạo bảng game
        self.board = np.zeros((self.rows, self.cols), dtype=int)
        self.visible = np.zeros((self.rows, self.cols), dtype=bool)
        self.mines = set()

        # Đặt mỏ ngẫu nhiên
        while len(self.mines) < self.num_mines:
            r, c = random.randint(0, self.rows - 1), random.randint(0, self.cols - 1)
            if (r, c) not in self.mines:
                self.mines.add((r, c))
                self.board[r, c] = -1  # -1 đại diện cho mỏ

        # Tính số mỏ xung quanh mỗi ô
        for r in range(self.rows):
            for c in range(self.cols):
                if self.board[r, c] == -1:
                    continue
                self.board[r, c] = sum((nr, nc) in self.mines for nr in range(r-1, r+2) for nc in range(c-1, c+2) if 0 <= nr < self.rows and 0 <= nc < self.cols)

        return self.get_observation()

    def get_observation(self):
        obs = np.where(self.visible, self.board, -2)
        return obs

    def step(self, action):
        r, c = action
        if (r, c) in self.mines:
            reward = -100
            done = True
            self.visible[r, c] = True
            streak_reset = True
        else:
            reward = 50
            self._reveal(r, c)
            done = np.all((self.visible) | (self.board == -1))
            if done:
                reward += 600
            streak_reset = False

        return self.get_observation(), reward, done, streak_reset

    def _reveal(self, r, c):
        # Hàm mở ô và mở các ô trống lân cận nếu có
        if not (0 <= r < self.rows and 0 <= c < self.cols) or self.visible[r, c]:
            return
        self.visible[r, c] = True
        if self.board[r, c] == 0:  # Mở các ô trống liên tiếp
            for nr in range(r-1, r+2):
                for nc in range(c-1, c+2):
                    if (nr, nc) != (r, c):
                        self._reveal(nr, nc)
    def is_finished(self):
        for r in range(self.rows):
            for c in range(self.cols):
                if self.visible[r][c] and self.board[r][c] == -1:
                    for rr in range(self.rows):
                        for cc in range(self.cols):
                            if self.board[rr][cc] == -1:
                                self.visible[rr][cc] = True
                    return -1

        non_mine_cells = (self.rows * self.cols) - self.num_mines
        revealed_cells = sum(
            1 for r in range(self.rows) for c in range(self.cols) if self.visible[r][c]
        )

        if revealed_cells == non_mine_cells:
            return 1

        return 0

    def remaining_mines(self):
        return self.num_mines - np.sum(self.board[self.visible] == -1)

